import React, { useState, useEffect, useCallback } from 'react';
import { getFlights } from '../services/api';
import FlightCard from '../components/FlightCard';
import Payment from '../components/Payment';

const Flights = () => {
  const [flights, setFlights] = useState([]);
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [selectedFlight, setSelectedFlight] = useState(null);
  const [seats, setSeats] = useState(1);

  // Fetch flights function wrapped with useCallback
  const fetchFlights = useCallback(async () => {
    try {
      const { data } = await getFlights({ source: from, destination: to });
      setFlights(data);
    } catch (err) {
      console.error(err);
    }
  }, [from, to]); // dependencies: run again if 'from' or 'to' changes

  // Handle search form submission
  const handleSearch = (e) => {
    e.preventDefault();
    fetchFlights();
  };

  // Handle flight selection
  const handleSelectFlight = (flight) => {
    setSelectedFlight(flight);
    setSeats(1); // Reset seats on new selection
  };

  // Fetch all flights on component mount
  useEffect(() => {
    fetchFlights();
  }, [fetchFlights]); // ✅ no ESLint warning

  return (
    <div style={{ padding: '1rem' }}>
      <h2>Search Flights</h2>

      <form onSubmit={handleSearch} style={{ marginBottom: '1rem' }}>
        <input
          type="text"
          placeholder="From"
          value={from}
          onChange={(e) => setFrom(e.target.value)}
          style={{ marginRight: '0.5rem', padding: '0.5rem' }}
        />
        <input
          type="text"
          placeholder="To"
          value={to}
          onChange={(e) => setTo(e.target.value)}
          style={{ marginRight: '0.5rem', padding: '0.5rem' }}
        />
        <button type="submit" style={{ padding: '0.5rem 1rem' }}>Search</button>
      </form>

      <div>
        {flights.length > 0 ? (
          flights.map((flight) => (
            <FlightCard
              key={flight._id}
              flight={flight}
              onSelect={() => handleSelectFlight(flight)}
            />
          ))
        ) : (
          <p>No flights found</p>
        )}
      </div>

      {selectedFlight && (
        <div style={{ marginTop: '2rem', padding: '1rem', border: '1px solid #ccc', borderRadius: '8px' }}>
          <h3>Booking for: {selectedFlight.airline} - {selectedFlight.flightNumber}</h3>
          <p>From: {selectedFlight.source} To: {selectedFlight.destination}</p>
          <p>Price per seat: ₹{selectedFlight.price}</p>
          <input
            type="number"
            min="1"
            max={selectedFlight.seatsAvailable}
            value={seats}
            onChange={(e) => setSeats(Number(e.target.value))}
            style={{ margin: '0.5rem 0', padding: '0.5rem', width: '80px' }}
          />
          <p>Total: ₹{selectedFlight.price * seats}</p>

          {/* Stripe Payment */}
          <Payment amount={selectedFlight.price * seats * 100} />
        </div>
      )}
    </div>
  );
};

export default Flights;
