import React from "react";
import { redirect } from "react-router-dom";

const Home = () => (
  <div style={{ textAlign: "center", padding: "150px" }}>
    <h1 style={{color:"black",fontWeight:"bold"}}>Welcome to Fly8Buddy ✈️</h1>
    <p style={{color:"black",fontWeight:"bold"}}>Book your flights easily and securely.</p>
    <a href="/flights">
      <button style={{
        backgroundColor: "#007bff",
        color: "white",
        padding: "10px 20px",
        border: "none",
        borderRadius: "8px",
        cursor: "pointer",
        marginTop: "20px"
      }}>
        View Flights
      </button>
    </a>
  </div>
);

export default Home;
