import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import "./Payment.css"; // Import the CSS file

const PaymentPage = () => {
  const { flightId } = useParams();
  const [flight, setFlight] = useState(null);
  const [loading, setLoading] = useState(true);
  const [seats, setSeats] = useState(1);
  const [paymentSuccess, setPaymentSuccess] = useState(false);

  useEffect(() => {
    const fetchFlight = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/flights/${flightId}`);
        const data = await res.json();
        setFlight(data);
      } catch (error) {
        console.error("Error fetching flight:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchFlight();
  }, [flightId]);

  const handlePayment = () => {
    setPaymentSuccess(true);
  };

  if (loading) {
    return (
      <div className="payment-loading">
        Loading flight details...
      </div>
    );
  }

  if (paymentSuccess) {
    return (
      <div className="payment-success">
        <div className="payment-success-box">
          <h2>✅ Payment Successful!</h2>
          <p>
            Your booking for <span>{flight.airline}</span> ({flight.flightNumber}) is confirmed.
          </p>
          <a href="/flights" className="back-button">Back to Flights</a>
        </div>
      </div>
    );
  }

  return (
    <div className="payment-container">
      <div className="payment-card">
        <h1 className="payment-title">✈️ Payment</h1>

        {flight ? (
          <>
            <div className="flight-details">
              <h2>{flight.airline} ({flight.flightNumber})</h2>
              <p> {flight.source} → {flight.destination}</p>
              <p><strong style={{color:"black"}}>Departure:</strong> {flight.departureTime}</p>
              <p><strong style={{color:"black"}}>Arrival:</strong> {flight.arrivalTime}</p>
              <p className="price">₹{flight.price} per seat</p>
            </div>

            <div className="seat-selection">
              <label>Number of Seats</label>
              <input
                type="number"
                min="1"
                value={seats}
                onChange={(e) => setSeats(e.target.value)}
              />
            </div>

            <div className="total-section">
              <h3>Total: ₹{(flight.price * seats).toLocaleString()}</h3>
            </div>

            <button onClick={handlePayment} className="pay-button">
              Proceed to Pay
            </button>
          </>
        ) : (
          <p className="not-found">Flight not found</p>
        )}
      </div>
    </div>
  );
};

export default PaymentPage;
