import React, { useContext, useState } from "react";
import { Link } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import { FaBars, FaTimes } from "react-icons/fa"; // for menu icons
import "./NavBar.css";

const Navbar = () => {
  const { user, logout } = useContext(AuthContext);
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const closeMenu = () => {
    setMenuOpen(false);
  };

  return (
    <nav className="navbar">
      {/* ===== LOGO / BRAND ===== */}
      <div className="nav-brand">
        <Link to="/" onClick={closeMenu}>✈️ FlightBooking</Link>
      </div>

      {/* ===== HAMBURGER ICON (MOBILE ONLY) ===== */}
      <div className="menu-icon" onClick={toggleMenu}>
        {menuOpen ? <FaTimes /> : <FaBars />}
      </div>

      {/* ===== NAV LINKS ===== */}
      <div className={`nav-links ${menuOpen ? "active" : ""}`}>
        <div className="nav-left">
          <Link to="/" onClick={closeMenu}>Home</Link>
          <Link to="/flights" onClick={closeMenu}>Flights</Link>
          {user && <Link to="/mybookings" onClick={closeMenu}>My Bookings</Link>}
        </div>

        <div className="nav-right">
          {user ? (
            <>
              <span>Welcome, {user.name}</span>
              <button onClick={() => { logout(); closeMenu(); }}>Logout</button>
            </>
          ) : (
            <>
              <Link to="/login" onClick={closeMenu}>Login</Link>
              <Link to="/register" onClick={closeMenu}>Register</Link>
            </>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
