import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const PaymentPage = () => {
  const { flightId } = useParams();
  const [flight, setFlight] = useState(null);
  const [loading, setLoading] = useState(true);
  const [seats, setSeats] = useState(1);
  const [paymentSuccess, setPaymentSuccess] = useState(false);

  useEffect(() => {
    const fetchFlight = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/flights/${flightId}`);
        const data = await res.json();
        setFlight(data);
      } catch (error) {
        console.error("Error fetching flight:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchFlight();
  }, [flightId]);

  const handlePayment = () => {
    setPaymentSuccess(true);
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-screen text-gray-600 text-xl">
        Loading flight details...
      </div>
    );
  }

  if (paymentSuccess) {
    return (
      <div className="flex flex-col justify-center items-center h-screen bg-green-50">
        <div className="bg-white shadow-lg p-8 rounded-2xl text-center">
          <h2 className="text-2xl font-semibold text-green-700 mb-3">
            ✅ Payment Successful!
          </h2>
          <p className="text-gray-600 mb-6">
            Your booking for <span className="font-medium">{flight.airline}</span> (
            {flight.flightNumber}) is confirmed.
          </p>
          <a
            href="/flights"
            className="bg-blue-600 text-white px-5 py-2 rounded-lg hover:bg-blue-700 transition"
          >
            Back to Flights
          </a>
        </div>
      </div>
    );
  }

  return (
    <div style={{justifyItems:"center"}} className="min-h-screen flex justify-center items-center bg-gray-100">
      {/* Centered payment box */}
      <div className="bg-white shadow-2xl rounded-3xl p-8 w-full max-w-lg border border-gray-200">
        <h1 className="text-3xl font-bold text-center text-blue-700 mb-6">
          ✈️ Payment
        </h1>

        {flight ? (
          <div >
            {/* Flight Details */}
            <div className="mb-6 border-b pb-4">
              <h2 className="text-3xl font-semibold text-gray-800 mb-2">
                {flight.airline} ({flight.flightNumber})
              </h2>
              <p className="text-gray-700" style={{fontSize:"20px",color:"black",fontWeight:"bold"}}>
                 {flight.source} →{" "}
                 {flight.destination}
              </p>
              <p className="text-gray-600 mt-1" style={{fontSize:"20px",color:"black",fontWeight:"bold"}}>
                <span className="font-medium" style={{fontSize:"20px",color:"#2b053cff",fontWeight:"bold"}}>Departure:</span>{" "}
                {flight.departureTime}
              </p>
              <p className="text-gray-600" style={{fontSize:"20px",color:"black",fontWeight:"bold"}}>
                <span className="font-medium" style={{fontSize:"20px",color:"#2b053cff",fontWeight:"bold"}}>Arrival:</span> {flight.arrivalTime}
              </p>
              <p className="text-lg font-semibold text-blue-700 mt-3" style={{fontSize:"20px",color:"black",fontWeight:"bold"}}>
                ₹{flight.price} per seat
              </p>
            </div>

            {/* Seat Selection */}
            <div className="mb-6">
              <label className="block text-gray-700 font-medium mb-2" style={{fontSize:"20px",color:"red",fontWeight:"bold"}}>
                Number of Seats 
              </label>
              <input
                type="number"
                min="1"
                value={seats}
                onChange={(e) => setSeats(e.target.value)}
                className="w-full border border-gray-300 rounded-lg px-3 py-2 text-black focus:outline-none focus:ring-2 focus:ring-blue-400"
                style={{color:"black"}}
              />
            </div>

            {/* Total and Button */}
            

            <button
              onClick={handlePayment}
              className="w-full bg-blue-600 text-white py-3 rounded-lg text-lg font-medium hover:bg-blue-700 transition transform hover:scale-105"
              style={{color:"red",fontWeight:"bold",textAlign:"center",padding:10,borderRadius:50,marginLeft:"70px",cursor:"pointer"}}>
              Pay {(flight.price * seats).toLocaleString()}
            </button>
          </div>
        ) : (
          <p className="text-center text-gray-500">Flight not found</p>
        )}
      </div>
    </div>
  );
};

export default PaymentPage;
