import React, { useState } from 'react';
import BookingForm from './BookingForm';

const FlightCard = ({ flight }) => {
  const [showBooking, setShowBooking] = useState(false);

  return (
    <div style={{ border: '1px solid gray', margin: '10px', padding: '10px' }}>
      <h3>{flight.airline} - {flight.flightNumber}</h3>
      <p>From: {flight.from} | To: {flight.to}</p>
      <p>Departure: {new Date(flight.departureTime).toLocaleString()}</p>
      <p>Arrival: {new Date(flight.arrivalTime).toLocaleString()}</p>
      <p>Price: ${flight.price} | Seats Available: {flight.seatsAvailable}</p>
      <button onClick={() => setShowBooking(!showBooking)}>
        {showBooking ? 'Cancel Booking' : 'Book Now'}
      </button>
      {showBooking && <BookingForm flight={flight} />}
    </div>
  );
};

export default FlightCard;
