import React from 'react';

const FlightCard = ({ flight, onSelect }) => {
  return (
    <div 
      style={{
        border: '1px solid #ce0e0eff',
        padding: '1rem',
        marginBottom: '1rem',
        cursor: 'pointer',
        borderRadius: '8px',
        boxShadow: '2px 2px 6px rgba(0,0,0,0.1)',
      }}
      onClick={onSelect} // Call onSelect when clicked
    >
      <h4 style={{color:'black',fontWeight:'bold'}}>{flight.airline} - {flight.flightNumber}</h4>
      <p style={{color:'black',fontWeight:'bold'}}>From: {flight.source} To: {flight.destination}</p>
      <p style={{color:'black'}}>Departure: {new Date(flight.departureTime).toLocaleString()}</p>
      <p style={{color:'black'}}>Arrival: {new Date(flight.arrivalTime).toLocaleString()}</p>
      <p style={{color:'black'}}>Price: â‚¹{flight.price}</p>
      <p style={{color:'black'}}>Seats Available: {flight.seatsAvailable}</p>
    </div>
  );
};

export default FlightCard;
