import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import FlightCard from '../components/FlightCard';

const Flights = () => {
  const [flights, setFlights] = useState([]);
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [selectedFlight, setSelectedFlight] = useState(null);
  const [seats, setSeats] = useState(1);
  const [paymentInfo, setPaymentInfo] = useState({
    name: '',
    cardNumber: '',
    expiry: '',
    cvv: '',
  });

  // ‚úÖ Fetch flights (with token authorization)
  const fetchFlights = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        alert('Please log in to view flights');
        window.location.href = '/login';
        return;
      }

      const res = await axios.get('http://localhost:5000/api/flights', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
        params: {
          source: from,
          destination: to,
        },
      });

      setFlights(res.data);
    } catch (err) {
      console.error('‚ùå Error fetching flights:', err);
      alert('Failed to fetch flights. Please check backend or login again.');
    }
  }, [from, to]);

  // üîç Handle search
  const handleSearch = (e) => {
    e.preventDefault();
    fetchFlights();
  };

  // ‚úàÔ∏è Handle flight selection
  const handleSelectFlight = (flight) => {
    setSelectedFlight(flight);
    setSeats(1);
  };

  // üßæ Handle payment input
  const handlePaymentChange = (e) => {
    setPaymentInfo({ ...paymentInfo, [e.target.name]: e.target.value });
  };

  // üí≥ Handle dummy payment submission
  const handlePaymentSubmit = async (e) => {
    e.preventDefault();

    if (!paymentInfo.name || !paymentInfo.cardNumber || !paymentInfo.expiry || !paymentInfo.cvv) {
      alert('Please fill all payment details');
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const totalAmount = selectedFlight.price * seats;

      // Optional: Create a booking record in backend
      await axios.post(
        'http://localhost:5000/api/bookings',
        {
          flightId: selectedFlight._id,
          seats,
          totalPrice: totalAmount,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      alert(`‚úÖ Payment successful! Your booking for ${selectedFlight.airline} is confirmed.`);
      setSelectedFlight(null); // Reset
      setPaymentInfo({ name: '', cardNumber: '', expiry: '', cvv: '' });
    } catch (err) {
      console.error('‚ùå Payment/booking error:', err);
      alert('Booking failed. Please try again.');
    }
  };

  // üîÑ Fetch all flights on mount
  useEffect(() => {
    fetchFlights();
  }, [fetchFlights]);

  return (
    <div style={{ padding: '1rem' }}>
      <h2>‚úàÔ∏è Search Flights</h2>

      {/* Search form */}
      <form onSubmit={handleSearch} style={{ marginBottom: '1rem' }}>
        <input
          type="text"
          placeholder="From"
          value={from}
          onChange={(e) => setFrom(e.target.value)}
          style={{
            marginRight: '0.5rem',
            padding: '0.5rem',
            borderRadius: '6px',
            border: '1px solid #ccc',
          }}
        />
        <input
          type="text"
          placeholder="To"
          value={to}
          onChange={(e) => setTo(e.target.value)}
          style={{
            marginRight: '0.5rem',
            padding: '0.5rem',
            borderRadius: '6px',
            border: '1px solid #ccc',
          }}
        />
        <button
          type="submit"
          style={{
            padding: '0.5rem 1rem',
            backgroundColor: '#007bff',
            color: 'white',
            border: 'none',
            borderRadius: '6px',
            cursor: 'pointer',
          }}
        >
          Search
        </button>
      </form>

      {/* Flights List */}
      <div>
        {flights && flights.length > 0 ? (
          flights.map((flight) => (
            <FlightCard
              key={flight._id}
              flight={flight}
              onSelect={() => handleSelectFlight(flight)}
            />
          ))
        ) : (
          <p>No flights found</p>
        )}
      </div>

      {/* Selected Flight & Payment Form */}
      {selectedFlight && (
        <div
          style={{
            marginTop: '2rem',
            padding: '1rem',
            border: '1px solid #ccc',
            borderRadius: '8px',
            maxWidth: '400px',
          }}
        >
          <h3>
            Booking for: {selectedFlight.airline} - {selectedFlight.flightNumber}
          </h3>
          <p>
            From: {selectedFlight.source} ‚Üí To: {selectedFlight.destination}
          </p>
          <p>Price per seat: ‚Çπ{selectedFlight.price}</p>
          <input
            type="number"
            min="1"
            max={selectedFlight.seatsAvailable}
            value={seats}
            onChange={(e) => setSeats(Number(e.target.value))}
            style={{
              margin: '0.5rem 0',
              padding: '0.5rem',
              width: '80px',
              borderRadius: '6px',
              border: '1px solid #ccc',
            }}
          />
          <p>
            <strong>Total: ‚Çπ{selectedFlight.price * seats}</strong>
          </p>

          {/* Dummy Payment Form */}
          <form onSubmit={handlePaymentSubmit}>
            <input
              type="text"
              name="name"
              placeholder="Cardholder Name"
              value={paymentInfo.name}
              onChange={handlePaymentChange}
              required
              style={{ width: '100%', margin: '0.5rem 0', padding: '0.5rem' }}
            />
            <input
              type="text"
              name="cardNumber"
              placeholder="Card Number"
              value={paymentInfo.cardNumber}
              onChange={handlePaymentChange}
              required
              style={{ width: '100%', margin: '0.5rem 0', padding: '0.5rem' }}
            />
            <input
              type="text"
              name="expiry"
              placeholder="MM/YY"
              value={paymentInfo.expiry}
              onChange={handlePaymentChange}
              required
              style={{ width: '100%', margin: '0.5rem 0', padding: '0.5rem' }}
            />
            <input
              type="password"
              name="cvv"
              placeholder="CVV"
              value={paymentInfo.cvv}
              onChange={handlePaymentChange}
              required
              style={{ width: '100%', margin: '0.5rem 0', padding: '0.5rem' }}
            />
            <button
              type="submit"
              style={{
                padding: '0.5rem 1rem',
                backgroundColor: 'green',
                color: 'white',
                border: 'none',
                borderRadius: '6px',
                cursor: 'pointer',
                width: '100%',
              }}
            >
              Pay Now
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

export default Flights;
