import React, { useEffect, useState } from 'react';
import { getUserBookings } from '../services/api';

const UserBookings = () => {
  const [bookings, setBookings] = useState([]);
  const userId = JSON.parse(localStorage.getItem('userId')) || null;

  useEffect(() => {
    const fetchBookings = async () => {
      if (!userId) return;
      try {
        const { data } = await getUserBookings(userId);
        setBookings(data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchBookings();
  }, [userId]);

  if (!userId) return <p>Please login to view your bookings.</p>;

  return (
    <div>
      <h2>My Bookings</h2>
      {bookings.length === 0 ? (
        <p>No bookings found</p>
      ) : (
        bookings.map((booking) => (
          <div key={booking._id} style={{ border: '1px solid gray', padding: '10px', margin: '10px' }}>
            <h3>{booking.flight.airline} - {booking.flight.flightNumber}</h3>
            <p>From: {booking.flight.from} | To: {booking.flight.to}</p>
            <p>Departure: {new Date(booking.flight.departureTime).toLocaleString()}</p>
            <p>Arrival: {new Date(booking.flight.arrivalTime).toLocaleString()}</p>
            <p>Seats Booked: {booking.seatsBooked}</p>
            <p>Total Price: ${booking.totalPrice}</p>
          </div>
        ))
      )}
    </div>
  );
};

export default UserBookings;
