import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const PaymentPage = () => {
  const { flightId } = useParams();
  const [flight, setFlight] = useState(null);
  const [paymentInfo, setPaymentInfo] = useState({ name: '', cardNumber: '', expiry: '', cvv: '' });
  const navigate = useNavigate();

  useEffect(() => {
    const fetchFlight = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/flights/${flightId}`);
        const data = await res.json();
        setFlight(data);
      } catch (error) {
        console.error(error);
      }
    };
    fetchFlight();
  }, [flightId]);

  const handleChange = (e) => {
    setPaymentInfo({ ...paymentInfo, [e.target.name]: e.target.value });
  };

  const handlePayment = (e) => {
    e.preventDefault();
    alert(`✅ Payment Successful for ${flight.airline} (${flight.flightNumber})`);
    navigate('/bookings');
  };

  if (!flight) return <p>Loading flight details...</p>;

  return (
    <div style={{ padding: '20px' }}>
      <h2>Payment Page</h2>
      <h3>{flight.airline} - {flight.flightNumber}</h3>
      <p>From: {flight.source} → To: {flight.destination}</p>
      <p>Price: ₹{flight.price}</p>
      <hr />

      <form onSubmit={handlePayment} style={{ maxWidth: '400px' }}>
        <input
          type="text"
          name="name"
          placeholder="Cardholder Name"
          value={paymentInfo.name}
          onChange={handleChange}
          required
        /><br /><br />

        <input
          type="text"
          name="cardNumber"
          placeholder="Card Number"
          value={paymentInfo.cardNumber}
          onChange={handleChange}
          required
        /><br /><br />

        <input
          type="text"
          name="expiry"
          placeholder="MM/YY"
          value={paymentInfo.expiry}
          onChange={handleChange}
          required
        /><br /><br />

        <input
          type="password"
          name="cvv"
          placeholder="CVV"
          value={paymentInfo.cvv}
          onChange={handleChange}
          required
        /><br /><br />

        <button type="submit">Pay Now</button>
      </form>
    </div>
  );
};

export default PaymentPage;
