import React, { useState, useEffect } from 'react';
import { getFlights } from '../services/api';
import FlightCard from '../components/FlightCard';
import Payment from '../components/Payment';

const Flights = () => {
  const [flights, setFlights] = useState([]);
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [selectedFlight, setSelectedFlight] = useState(null);
  const [seats, setSeats] = useState(1);

  const fetchFlights = async () => {
    try {
      const { data } = await getFlights({ source: from, destination: to });
      setFlights(data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleSearch = (e) => {
    e.preventDefault();
    fetchFlights();
  };

  const handleSelectFlight = (flight) => {
    setSelectedFlight(flight);
    setSeats(1); // reset seats on new selection
  };

  useEffect(() => {
    fetchFlights(); // fetch all flights on page load
  }, []);

  return (
    <div>
      <h2>Search Flights</h2>
      <form onSubmit={handleSearch}>
        <input type="text" placeholder="From" value={from} onChange={e => setFrom(e.target.value)} />
        <input type="text" placeholder="To" value={to} onChange={e => setTo(e.target.value)} />
        <button type="submit">Search</button>
      </form>

      <div>
        {flights.length > 0 ? (
          flights.map(flight => (
            <FlightCard
              key={flight._id}
              flight={flight}
              onSelect={() => handleSelectFlight(flight)}
            />
          ))
        ) : (
          <p>No flights found</p>
        )}
      </div>

      {selectedFlight && (
        <div style={{ marginTop: '2rem' }}>
          <h3>Booking for: {selectedFlight.airline} - {selectedFlight.flightNumber}</h3>
          <p>From: {selectedFlight.source} To: {selectedFlight.destination}</p>
          <p>Price per seat: ₹{selectedFlight.price}</p>
          <input
            type="number"
            min="1"
            max={selectedFlight.seatsAvailable}
            value={seats}
            onChange={(e) => setSeats(Number(e.target.value))}
          />
          <p>Total: ₹{selectedFlight.price * seats}</p>

          {/* Stripe Payment */}
          <Payment amount={selectedFlight.price * seats * 100} />
        </div>
      )}
    </div>
  );
};

export default Flights;
