import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

// Get token from localStorage
const getAuthConfig = () => {
  const token = localStorage.getItem('token');
  return {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };
};

// User APIs
export const registerUser = (userData) => axios.post(`${API_URL}/users/register`, userData);
export const loginUser = (userData) => axios.post(`${API_URL}/users/login`, userData);

// Flight APIs
export const getFlights = (query) => axios.get(`${API_URL}/flights`, { ...getAuthConfig(), params: query });
export const createFlight = (flightData) => axios.post(`${API_URL}/flights`, flightData, getAuthConfig());

// Booking APIs
export const createBooking = (bookingData) => axios.post(`${API_URL}/bookings`, bookingData, getAuthConfig());
export const getUserBookings = (userId) => axios.get(`${API_URL}/bookings/${userId}`, getAuthConfig());
