import React, { useEffect, useState } from "react";
import "./MyBookings.css";

const MyBookings = () => {
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    const data = JSON.parse(localStorage.getItem("myBookings")) || [];
    setBookings(data);
  }, []);

  return (
    <div className="bookings-container">
      <h1>ðŸ§¾ My Bookings</h1>
      {bookings.length === 0 ? (
        <p>No bookings found.</p>
      ) : (
        <div className="booking-list" style={{color:"black"}}>
          {bookings.map((b) => (
            <div key={b.id} className="booking-card">
              <h3>
                {b.flight.airline} ({b.flight.flightNumber})
              </h3>
              <p>
                {b.flight.source} â†’ {b.flight.destination}
              </p>
              <p>Seats: {b.seats}</p>
              <p>Total Paid: â‚¹{b.total.toLocaleString()}</p>
              <p>Date: {b.date}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default MyBookings;
