import React, { useState, useEffect, useCallback } from 'react';
import { getFlights } from '../services/api';
import FlightCard from '../components/FlightCard';
import Payment from '../components/Payment';

const Flights = () => {
  const [flights, setFlights] = useState([]);
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [selectedFlight, setSelectedFlight] = useState(null);
  const [seats, setSeats] = useState(1);
  const [loading, setLoading] = useState(false);

  // Fetch flights based on search
  const fetchFlights = useCallback(async () => {
    try {
      setLoading(true);
      const response = await getFlights({ source: from, destination: to });
      const data = response?.data || response || [];
      setFlights(data);
    } catch (err) {
      console.error('‚ùå Error fetching flights:', err);
      alert('Unable to fetch flights. Try again later.');
    } finally {
      setLoading(false);
    }
  }, [from, to]);

  // Fetch all flights initially
  useEffect(() => {
    fetchFlights();
  }, [fetchFlights]);

  // Handle search form submission
  const handleSearch = (e) => {
    e.preventDefault();
    fetchFlights();
  };

  // When a user selects a flight card
  const handleSelectFlight = (flight) => {
    setSelectedFlight((prev) => (prev?._id === flight._id ? null : flight)); // toggle selection
    setSeats(1);
  };

  return (
    <div style={{ padding: '1.5rem' }}>
      <h2 style={{ marginBottom: '1rem' }}>‚úàÔ∏è Search and Book Flights</h2>

      {/* üîç Search Form */}
      <form onSubmit={handleSearch} style={{ marginBottom: '1rem' }}>
        <input
          type="text"
          placeholder="From"
          value={from}
          onChange={(e) => setFrom(e.target.value)}
          style={{ marginRight: '0.5rem', padding: '0.5rem' }}
        />
        <input
          type="text"
          placeholder="To"
          value={to}
          onChange={(e) => setTo(e.target.value)}
          style={{ marginRight: '0.5rem', padding: '0.5rem' }}
        />
        <button type="submit" style={{ padding: '0.5rem 1rem' }}>Search</button>
      </form>

      {/* ‚úàÔ∏è Flights List */}
      {loading ? (
        <p>Loading flights...</p>
      ) : flights.length > 0 ? (
        flights.map((flight) => (
          <div key={flight._id} style={{ marginBottom: '1rem' }}>
            {/* Flight Card */}
            <div
              onClick={() => handleSelectFlight(flight)}
              style={{
                border: '1px solid #ccc',
                borderRadius: '8px',
                padding: '1rem',
                cursor: 'pointer',
                backgroundColor:
                  selectedFlight?._id === flight._id ? '#f0f8ff' : '#fff',
                transition: 'background-color 0.3s ease',
              }}
            >
              <h3>{flight.airline} - {flight.flightNumber}</h3>
              <p>From: {flight.source} ‚Üí To: {flight.destination}</p>
              <p>Departure: {flight.departureTime}</p>
              <p>Price: ‚Çπ{flight.price}</p>
            </div>

            {/* üí≥ Payment Section (Opens only when selected) */}
            {selectedFlight?._id === flight._id && (
              <div
                style={{
                  marginTop: '1rem',
                  padding: '1rem',
                  border: '1px solid #ddd',
                  borderRadius: '8px',
                  backgroundColor: '#fafafa',
                }}
              >
                <h4>Complete Your Booking</h4>
                <p>Flight: {selectedFlight.airline} - {selectedFlight.flightNumber}</p>
                <p>Price per Seat: ‚Çπ{selectedFlight.price}</p>

                <label>
                  Seats:&nbsp;
                  <input
                    type="number"
                    min="1"
                    max={selectedFlight.seatsAvailable || 5}
                    value={seats}
                    onChange={(e) => setSeats(Number(e.target.value))}
                    style={{
                      padding: '0.4rem',
                      width: '70px',
                      marginRight: '1rem',
                    }}
                  />
                </label>

                <p>
                  Total: <strong>‚Çπ{selectedFlight.price * seats}</strong>
                </p>

                {/* Stripe Payment Integration */}
                <Payment amount={selectedFlight.price * seats * 100} />
              </div>
            )}
          </div>
        ))
      ) : (
        <p>No flights found. Try searching again.</p>
      )}
    </div>
  );
};

export default Flights;
