import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "./Payment.css";

const PaymentPage = () => {
  const { flightId } = useParams();
  const [flight, setFlight] = useState(null);
  const [loading, setLoading] = useState(true);
  const [seats, setSeats] = useState(1);
  const [showPaymentForm, setShowPaymentForm] = useState(false);
  const [paymentSuccess, setPaymentSuccess] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchFlight = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/flights/${flightId}`);
        const data = await res.json();
        setFlight(data);
      } catch (error) {
        console.error("Error fetching flight:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchFlight();
  }, [flightId]);

  const handleProceedToPay = () => {
    setShowPaymentForm(true);
  };

  const handlePaymentSubmit = (e) => {
    e.preventDefault();
    // Simulate dummy payment
    setTimeout(() => {
      setPaymentSuccess(true);
      setTimeout(() => navigate("/mybookings"), 2500); // redirect after success
    }, 1500);
  };

  if (loading) {
    return <div className="loading">Loading flight details...</div>;
  }

  if (paymentSuccess) {
    return (
      <div className="success-page">
        <div className="success-card" >
          <h2 style={{color:"black"}}>‚úÖ Payment Successful!</h2>
          <p style={{color:"black"}}>
            Your booking for <strong>{flight.airline}</strong> ({flight.flightNumber}) is confirmed.
          </p>
          <p className="redirect-text" style={{color:"black"}}>Redirecting to your bookings...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="payment-container">
      <div className="payment-card">
        <h1 className="payment-title">‚úàÔ∏è Flight Payment</h1>

        {flight ? (
          <>
            <div className="flight-details">
              <h2>{flight.airline} ({flight.flightNumber})</h2>
              <p>From: {flight.source} ‚Üí To: {flight.destination}</p>
              <p>Departure: {flight.departureTime}</p>
              <p>Arrival: {flight.arrivalTime}</p>
              <h3 className="price" style={{color:"black"}}>‚Çπ{flight.price} per seat</h3>
            </div>

            {!showPaymentForm ? (
              <>
                <div className="seat-section">
                  <label>Number of Seats</label>
                  <input
                    type="number"
                    min="1"
                    value={seats}
                    onChange={(e) => setSeats(e.target.value)}
                  />
                </div>

                <div className="total-section">
                  <h3 className="price">Total: ‚Çπ{(flight.price * seats).toLocaleString()}</h3>
                </div>

                <button onClick={handleProceedToPay} className="pay-btn">
                  Proceed to Pay
                </button>
              </>
            ) : (
              <form className="card-form" onSubmit={handlePaymentSubmit}>
                <h3>üí≥ Enter Card Details</h3>
                <label>Card Number</label>
                <input type="text" maxLength="16" required />

                <label>Card Holder Name</label>
                <input type="text" required />

                <div className="card-row">
                  <div>
                    <label>Expiry Date</label>
                    <input type="text" placeholder="MM/YY" maxLength="5" required />
                  </div>
                  <div>
                    <label>CVV</label>
                    <input type="password" maxLength="3" required />
                  </div>
                </div>

                <button type="submit" className="confirm-btn">
                  Confirm Payment
                </button>
              </form>
            )}
          </>
        ) : (
          <p className="notfound">Flight not found</p>
        )}
      </div>
    </div>
  );
};

export default PaymentPage;
